import { useState, useEffect, useCallback } from 'react';
import MovieCard from './MovieCard';
import Counter from './Counter';

function App() {
  const [search, setSearch] = useState('');
  const [movies, setMovies] = useState([]);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [loading, setLoading] = useState(false);
  const API_KEY = 'bbc1f8d1'; // Замени на свой OMDB API ключ

  const searchMovies = useCallback(async () => {
    setLoading(true);
    try {
      // Искусственная задержка для тестирования индикатора (удали в продакшене)
      // await new Promise(resolve => setTimeout(resolve, 1000));
      const response = await fetch(
        `http://www.omdbapi.com/?s=${encodeURIComponent(search)}&page=${page}&apikey=${API_KEY}`
      );
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      if (data.Response === 'True') {
        setMovies(page === 1 ? data.Search : [...movies, ...data.Search]); // Сохраняем предыдущие результаты
        setError('');
      } else {
        setMovies(page === 1 ? [] : movies);
        setError(data.Error || 'No movies found');
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setMovies(page === 1 ? [] : movies);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  }, [search, page, API_KEY, movies]);

  useEffect(() => {
    if (search) searchMovies();
  }, [search, page, searchMovies]);

  return (
    <div style={{ position: 'relative' }}>
      <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
        <h1>Movie Search</h1>
        <Counter />
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search for a movie..."
          style={{ width: '100%', padding: '10px', marginBottom: '20px' }}
        />
        <button
          onClick={() => {
            setSearch('');
            setMovies([]);
            setError('');
            setPage(1);
            setSelectedMovie(null);
            setLoading(false);
          }}
          style={{ padding: '10px 20px', marginBottom: '20px', background: '#007bff', color: 'white', border: 'none', borderRadius: '5px' }}
        >
          Clear Search
        </button>
        {loading && <p style={{ fontWeight: 'bold', color: '#007bff' }}>Loading...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {movies.length > 0 ? (
          movies.map((movie, index) => (
            <MovieCard
              key={index}
              title={movie.Title}
              year={movie.Year}
              poster={movie.Poster}
              imdbID={movie.imdbID}
              onClick={async () => {
                try {
                  setLoading(true);
                  const response = await fetch(`http://www.omdbapi.com/?i=${movie.imdbID}&apikey=${API_KEY}`);
                  if (!response.ok) throw new Error('Failed to fetch movie details');
                  const data = await response.json();
                  if (data.Response === 'True') setSelectedMovie(data);
                  else setError(data.Error || 'Failed to load movie details');
                } catch (error) {
                  setError(error.message);
                } finally {
                  setLoading(false);
                }
              }}
            />
          ))
        ) : (
          !loading && !error && <p>No movies found</p>
        )}
        {movies.length > 0 && (
          <button
            onClick={() => setPage(page + 1)}
            style={{ padding: '10px 20px', marginTop: '20px', background: '#007bff', color: 'white', border: 'none', borderRadius: '5px' }}
            disabled={loading}
          >
            Load More
          </button>
        )}
      </div>
      {selectedMovie && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
        >
          <div
            style={{
              backgroundColor: 'white',
              padding: '20px',
              borderRadius: '5px',
              maxWidth: '500px',
              width: '90%',
              maxHeight: '80vh',
              overflowY: 'auto',
            }}
          >
            <h2>{selectedMovie.Title || 'No Title'}</h2>
            <p><strong>Plot:</strong> {selectedMovie.Plot || 'No Plot Available'}</p>
            <p><strong>Director:</strong> {selectedMovie.Director || 'N/A'}</p>
            <p><strong>Actors:</strong> {selectedMovie.Actors || 'N/A'}</p>
            <p><strong>Genre:</strong> {selectedMovie.Genre || 'N/A'}</p>
            <p><strong>Released:</strong> {selectedMovie.Released || 'N/A'}</p>
            {selectedMovie.Poster && selectedMovie.Poster !== 'N/A' && (
              <img src={selectedMovie.Poster} alt={selectedMovie.Title} style={{ maxWidth: '100%', borderRadius: '5px' }} />
            )}
            <button
              onClick={() => setSelectedMovie(null)}
              style={{ padding: '10px 20px', background: '#dc3545', color: 'white', border: 'none', borderRadius: '5px' }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
