return (
<div style={{ display:'flex', gap:'20px', borderBottom:'1px solid #ccc', padding:'10px 0'}}>
    {poster && <img src={poster} alt={title} style={{maxWidth:'100px'}}/>}
<div>
<h3>{title}</h3>
<p>Year: {year}</p>
</div>
</div>
);
}

