import { useState, useEffect, useCallback } from 'react';
import MovieCard from './MovieCard';
import Counter from './Counter';

function App() {
  const [search, setSearch] = useState('');
  const [movies, setMovies] = useState([]);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1); // Добавлено состояние page
  const API_KEY = 'bbc1f8d1'; // Замени на свой OMDB API ключ
  const [selectedMovie, setSelectedMovie] = useState(null);
  const searchMovies = useCallback(async () => {
    try {
      const response = await fetch(
        `http://www.omdbapi.com/?s=${encodeURIComponent(search)}&page=${page}&apikey=${API_KEY}`
      );
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      if (data.Response === 'True') {
        setMovies(data.Search);
        setError('');
      } else {
        setMovies([]);
        setError(data.Error || 'No movies found');
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setMovies([]);
      setError(error.message);
    }
  }, [search, page, API_KEY]);

  useEffect(() => {
    if (search) searchMovies();
  }, [search, page, searchMovies]);

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>Movie Search</h1>
      <Counter />
      <input
        type="text"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        placeholder="Search for a movie..."
        style={{ width: '100%', padding: '10px', marginBottom: '20px' }}
      />
      <button
        onClick={() => {
          setSearch('');
          setMovies([]);
          setError('');
          setPage(1); // Сбрасываем страницу
        }}
        style={{ padding: '10px 20px', marginBottom: '20px' }}
      >
        Clear Search
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {movies.length > 0 ? (
        movies.map((movie, index) => (
          <MovieCard
            key={index}
            title={movie.Title}
            year={movie.Year}
            poster={movie.Poster}
            imdbID={movie.imdbID}
            onClick={async () => {
              const response = await fetch(`http://www.omdbapi.com/?i=${movie.imdbID}&apikey=${API_KEY}`);
              const data = await response.json();
              setSelectedMovie(data);
            }}
          />
        ))
      ) : (
        !error && <p>No movies found</p>
      )}
      {movies.length > 0 && (
        <button
          onClick={() => setPage(page + 1)}
          style={{ padding: '10px 20px', marginTop: '20px' }}
        >
          Load More
        </button>
      )}
    </div>
  );
}

export default App;
